var assert=require("better-assert");var expect=require("expect.js");var parsejson=require("./index.js");describe("my suite",function(){it("should parse a JSON string",function(){var a='{"users" :[{"first_name":"foo", "last_name":"bar"}],"id"    :40,"cities":["los angeles", "new york", "boston"]}';var b=parsejson(a);expect(b.users[0].first_name).to.be("foo");expect(b.users[0].last_name).to.be("bar");expect(b.id).to.be(40);expect(b.cities[0]).to.be("los angeles");expect(b.cities[1]).to.be("new york");expect(b.cities[2]).to.be("boston")})});