var assert=require("better-assert");var expect=require("expect.js");var util=require("./index.js");describe("querystring test suite",function(){it("should parse a querystring and return an object",function(){var a=util.decode("foo=bar");expect(a.foo).to.be("bar");a=util.decode("france=paris&germany=berlin");expect(a.france).to.be("paris");expect(a.germany).to.be("berlin");a=util.decode("india=new%20delhi");expect(a.india).to.be("new delhi")});it("should construct a query string from an object",function(){expect(util.encode({a:"b"})).to.be("a=b");expect(util.encode({a:"b",c:"d"})).to.be("a=b&c=d");expect(util.encode({a:"b",c:"tobi rocks"})).to.be("a=b&c=tobi%20rocks")})});